# include "../../common.h"


class Solution {
public:
    void find_two_sum(vector<int> &nums, int start, int end, int target,set<vector<int>>& ans,vector <vector<int>>& anst){
        while (start<end) {
            if (nums[start] + nums[end] == target) {
                vector<int> yo={-target,nums[start],nums[end]};
                if(ans.find ({-target,nums[start],nums[end]})==ans.end ()){
                    anst.emplace_back (yo);
                    ans.insert ({-target,nums[start],nums[end]});
                }

                while(start<end && nums[start]==nums[start-1]){
                    start++;
                }
//                return true;
            }
            if (nums[start] + nums[end] > target) {
                end--;
            }else{
                start++;
            }
        }
//        return false;
    }
    vector <vector<int>> threeSum (vector<int> &nums) {
        vector <vector<int>> ans;
        set<vector<int>> anst;
        if(nums.size ()<=2){
            return ans;
        }
        sort(nums.begin(), nums.end());
        for (int i = 0; i < nums.size ()-2; ++i) {
            find_two_sum (nums,i+1,nums.size ()-1,-nums[i],anst,ans);
        }
        return ans;
    }
};

